import { Component, OnInit } from '@angular/core';


interface Producto {
  nombre: string;
  imagen: string;
  cantidad: number;
  precio: number;
  }


@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent implements OnInit {
    carrito: Producto[] = [
    { nombre: 'Producto 1', imagen: 'data:image/jpeg;base64,', cantidad: 1, precio: 10},
    { nombre: 'Producto 2', imagen: 'data:image/jpeg;base64,', cantidad: 3, precio: 10},
    { nombre: 'Producto 3', imagen: 'data:image/jpeg;base64,', cantidad: 2, precio: 10},
    { nombre: 'Producto 3', imagen: 'data:image/jpeg;base64,', cantidad: 2, precio: 10},
    { nombre: 'Producto 3', imagen: 'data:image/jpeg;base64,', cantidad: 2, precio: 10}

  ];

  constructor() { }

  ngOnInit(): void {
  }


  aumentarCantidad(producto: Producto): void {
    producto.cantidad++;
  }

  disminuirCantidad(producto: Producto): void {
    if (producto.cantidad > 1) {
      producto.cantidad--;
    }
  }

  calcularTotal(): number {
    return this.carrito.reduce((total, producto) => total + producto.precio * producto.cantidad, 0);
  }

  limpiarCarrito(): void {
    this.carrito = [];
  }
}
